const hover=(()=>{var gridItem=document.getElementsByClassName("grid-wrapper");gridItem[0].addEventListener("mouseover",event=>{const currentNode=event.target.parentNode.lastChild;"grid-title"===currentNode.className&&(currentNode.style.opacity="1")}),gridItem[0].addEventListener("mouseout",event=>{const currentNode=event.target.parentNode.lastChild;"grid-title"===currentNode.className&&(currentNode.style.opacity="0")})})();